function searchProduct(){let e=document.getElementsByTagName("input")[0].value.trim().toLowerCase();fetch("jsons/Search.json").then((e=>e.json())).then((t=>{let r=t.Data.SearchResponse.Documents,a=document.getElementsByClassName("content")[0];if(e.length>=0){a.innerHTML="";r.filter((t=>t.ProductName.trim().toLowerCase().startsWith(e))).forEach((e=>{let t=imgTagCreater(e.ProductPictures[0].ProductPicturePath),r=aTagCreater(e.ProductLink);r.appendChild(t),a.appendChild(r)}))}else{if(e)return;for(let e=0;e<r.length;e++){let t=imgTagCreater(r[e].ProductPictures[0].ProductPicturePath),n=aTagCreater(r[e].ProductLink);n.appendChild(t),a.appendChild(n)}}})).catch((e=>alert(e.message)))}